import React, { useState } from "react";
import TaskInput from "./components/TaskInput";
import TaskList from "./components/TaskList";
import Tabs from "./components/Tabs";
import { categorizeTask } from "./utils/genreCategorizer";
import Calendar from "react-calendar";
import "./App.css";
import "react-calendar/dist/Calendar.css";

function App() {
  const [tasks, setTasks] = useState([]);
  const [filter, setFilter] = useState("all");
  const [genres, setGenres] = useState([
    "授業",
    "課題",
    "テスト勉強",
    "アルバイト",
    "買い物",
    "友人",
    "就活",
    "趣味",
  ]);
  const [selectedGenre, setSelectedGenre] = useState("all");
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [selectedDate, setSelectedDate] = useState(null);

  const addGenre = (newGenre) => {
    if (!genres.includes(newGenre)) {
      setGenres([...genres, newGenre]);
    }
  };

  // TaskInputに addGenre を渡す
  <TaskInput addTask={addTask} addGenre={addGenre} genres={genres} />

  const addTask = async (taskText, deadline) => {
    const dynamicGenres = genres.join("、");
    const genre = await categorizeTask(taskText, dynamicGenres);

    if (!genres.includes(genre)) {
      setGenres([...genres, genre]);
    }

    const newTask = {
      id: Date.now(),
      text: taskText,
      genre: genre || "その他",
      deadline,
      completed: false,
    };
    setTasks([...tasks, newTask]);
  };

  const toggleTaskCompletion = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const toggleTheme = () => {
    setIsDarkMode(!isDarkMode);
    document.body.className = isDarkMode ? "light-mode" : "dark-mode";
  };

  const filteredTasks = tasks.filter((task) => {
    if (filter === "completed") return task.completed;
    if (filter === "incomplete") return !task.completed;
    if (selectedGenre !== "all") return task.genre === selectedGenre;
    return true;
  });

  return (
    <div className={`app ${isDarkMode ? "dark-mode" : "light-mode"}`}>
      {/* カレンダーを枠外に配置 */}
      <div className="calendar-outside">
        <Calendar
          onChange={(date) => setSelectedDate(date)}
          value={selectedDate}
          tileContent={({ date }) => {
            const hasTask = tasks.some(
              (task) => task.deadline === date.toISOString().split("T")[0]
            );
            return hasTask ? <div className="calendar-dot"></div> : null;
          }}
        />
      </div>

      {/* 枠内に収めたUI */}
      <div className="app-container">
        <div className="header">
          <h1>Ultimate TODO</h1>
          <button className="theme-toggle" onClick={toggleTheme}>
            {isDarkMode ? "☀️" : "🌙"}
          </button>
        </div>

        <TaskInput addTask={addTask} />
        <Tabs setFilter={setFilter} setSelectedGenre={setSelectedGenre} genres={genres} />
        <h2>全てのタスク</h2>
        <TaskList
          tasks={filteredTasks}
          toggleTaskCompletion={toggleTaskCompletion}
          deleteTask={deleteTask}
        />
      </div>
    </div>
  );
}

export default App;
